mapper.select:
select * from tb_notice_production

    and id = #{obj.id}
    and creat_time = #{obj.creatTime}
    and update_time = #{obj.updateTime}
    and state = #{obj.state}
    and visibility = #{obj.visibility}
    and notice_number = #{obj.notice_number}
    and working_number = #{obj.working_number}
    and order_unit = #{obj.orderUnit}
    and delivery_method = #{obj.deliveryMethod}
    and delivery_state = #{obj.deliveryState}
    and delivery_place = #{obj.deliveryPlace}
    and is_in_batches = #{obj.isInBatches}
    and delivery_date = #{obj.deliveryDate}
    and remark = #{obj.remark}
    and compiling_person = #{obj.compilingPerson}
    and submit_time = #{obj.submitTime}
    and auditor = #{obj.auditor}
    and audit_time = #{obj.auditTime}
    and audit_remark = #{obj.auditRemark}
    and approver = #{obj.approver}
    and approve_time = #{obj.approveTime}
    and approve_remark = #{obj.approveRemark}

limit #{obj.page}, #{obj.pageSize}

