mapper.select:
select * from tb_product

    and id = #{obj.id}
    and creat_time = #{obj.creatTime}
    and update_time = #{obj.updateTime}
    and drawing_number = #{obj.drawingNumber}
    and name = #{obj.name}
    and material_quality = #{obj.materialQuality}
    and type = #{obj.type}

limit #{obj.page}, #{obj.pageSize}

mapper.update:
update tb_product

    and id = #{data.id}
    and creat_time = #{data.creatTime}
    and update_time = #{data.updateTime}
    and drawing_number = #{data.drawingNumber}
    and name = #{data.name}
    and material_quality = #{data.materialQuality}
    and type = #{data.type}


    and id = #{condition.id}
    and creat_time = #{condition.creatTime}
    and update_time = #{condition.updateTime}
    and drawing_number = #{condition.drawingNumber}
    and name = #{condition.name}
    and material_quality = #{condition.materialQuality}
    and type = #{condition.type}

mapper.delete:
delete from tb_product

    and id = #{obj.id}
    and creat_time = #{obj.creatTime}
    and update_time = #{obj.updateTime}
    and drawing_number = #{obj.drawingNumber}
    and name = #{obj.name}
    and material_quality = #{obj.materialQuality}
    and type = #{obj.type}
