mapper.select:
select * from

     #{obj.viewName}
     tb_product_producing


    and id = #{obj.id}
    and creat_time = #{obj.creatTime}
    and update_time = #{obj.updateTime}
    and creater = #{obj.creater}
    and type = #{obj.type}
    and state = #{obj.state}
    and notice_id = #{obj.noticeId}
    and notice_number = #{obj.noticeNumber}
    and process_version_id = #{obj.processVersionId}
    and process_version_number = #{obj.processVersionNumber}
    and execution_id = #{obj.executionId}
    and order_number = #{obj.orderNumber}
    and drawing_number = #{obj.drawingNumber}
    and product_name = #{obj.productName}
    and material_quality = #{obj.materialQuality}
    and  = #{obj.}
    and delivery_date = #{obj.deliveryDate}
    and remark = #{obj.remark}

limit #{obj.page}, #{obj.pageSize}

